import React from 'react';
import Bar from './Bar.js';
import generateBars from './generateBars.js'

class Bars extends React.Component{
    constructor(){
        super()
        this.state={bars: generateBars()}

        this.sortBars=this.sortBars.bind(this)
    }

    renderBar(val,index){
        return (
        <Bar key={index} val={val.height} color={val.color}/>
        );
    }

    sortBars(){
        let newBars = JSON.parse(JSON.stringify(this.state))
        var a=0;
        for (let i=0;i<newBars.bars.length-1;i++){
            // if (newBars.bars[i].height<newBars.bars[i+1].height){
            setTimeout( () => {newBars.bars[a].color="red";this.setState(newBars);console.log(this.state)},13*a)
            setTimeout( () => {newBars.bars[a].color="black";this.setState(newBars);console.log(this.state)}, 13*a+30)
            a++
        }
    }

    render(){
        return(
            <>
            <button onClick={this.sortBars}>Sort</button>
            {this.state.bars.map( (val,index) => this.renderBar(val,index))}

            </>
        );
    }
}

