import React from 'react';
import bars from './generateBars.js';


class Bars extends React.Component{
    constructor(){
        super()
        this.state={bars: bars,colour: bars.map( () => `black`)}
        console.log(this.state);



        this.sortBars=this.sortBars.bind(this)
    }

    sortBars(){
        var colours= this.state.colour
        for (var i=0; i<=this.state.bars.length-1;i++){
            colours[i]=`red`
            this.setState(
                {colour:colours}
            )
            console.log(this.state)
            setTimeout(1000)
        }
    }

    render(){
        return(
            <>
            <button onClick={this.sortBars}>Sort</button>
            {this.state.bars.map((height,index) => (
                <div key={index} style={{background: `black`, marginRight: `20px`, height: height, float: 'left', width: `20px`}}>   
                </div>
            ))}

            </>
        );
    }
}

