export default function linear_sort_animations(state){
    let stateCopy = JSON.parse(JSON.stringify(state))
    let animations=[]
    let sorted=false
    while(sorted=false){
        sorted=true
        for (let i=0;i<stateCopy.bars.length-1;i++){
            if (stateCopy[i].height<stateCopy[i+1].height){
                sorted=false
                let temp=stateCopy[i].height
                stateCopy[i].height=stateCopy[i+1].height
                stateCopy[i+1].height=temp
                animations.push([i,i+1])
            }

        }


    }
    return animations
}
