export default function linear_sort_animations(arr){
    console.log(arr)
    let animations=[]
    let sorted=false
    while(sorted=false){
        sorted=true
        for (let i=0;i<arr.length-1;i++){
            if (arr[i]<arr[i+1]){
                let temp=arr[i]
                arr[i]=arr[i+1]
                arr[i+1]=temp
                sorted=false
                animations.push([i,i+1])
            }

        }


    }
    return animations
}
