import Bar from './Bar.js';
import bars from './generateBars.js'

class Bars extends React.Component{
    constructor(){
        super()
        this.state={bars: bars}

        this.sortBars=this.sortBars.bind(this)
    }

    renderBar(val,index){
        return (
        <Bar key={index} val={val}/>
        );
    }

    sortBars(){
        const newBars=this.state.bars.slice
        newBars[0]=1000
        console.log(newBars[0])
        console.log(this.state.bars[0])
        // this.setState({bars: newBars})
    }

    render(){
        return(
            <>
            <button onClick={this.sortBars}>Sort</button>
            {this.state.bars.map( (val,index) => this.renderBar(val,index))}

            </>
        );
    }
}

