import React from 'react';
import bars from './bars.js';
import Bar from './Bar.js';

function App() {
  const b = bars.map(item => <Bar width={item.width} key={item.id} />)
  console.log(b[0]);

  function sortBars(){
    return b[0].changeWidth(200);
  }

  return (
    <div className="App">
      <button onClick={sortBars}>Start</button>
      {b}
    </div>

  );
}

export default App;
