export default function merge_sort_animations(arr){
let animations=[]
merge(arr,arr,animations)
return animations
}



function merge(arr,auxiliaryArr,animations){
    if (auxiliaryArr.length > 1){
        let midIndx=Math.floor(auxiliaryArr.length/2)
        let L = auxiliaryArr.slice(0,midIndx)
        let R= auxiliaryArr.slice(midIndx)
        console.log(L,R)
        merge(arr,L,animations)
        merge(arr,R,animations)
        let i,j,k=0

        while(i<L.length && j<R.length){
            if (L[i]>R[j]){
                arr[k]=L[i]
                i++
            }
            else {
                arr[k]=R[j]
                j++
            }
            k++

        }

        while (i<L.length){
            arr[k]=L[i]
            i++
            k++
        }

        while (j<R.length){
            auxiliaryArr[k]=R[j]
            j++
            k++
        }
    }
}
