import React from 'react';
import Bar from './Bar.js';
import generateBars from './generateBars.js'

class Bars extends React.Component{
    constructor(){
        super()
        this.state={bars: generateBars()}

        this.sortBars=this.sortBars.bind(this)
    }

    renderBar(val,index){
        return (
        <Bar key={index} val={val.height} color={val.color}/>
        );
    }

    sortBars(){
        let newBars = JSON.parse(JSON.stringify(this.state))
        let a=1;
        let sorted=true
        for (let i=0;i<newBars.bars.length-1;i++){
            if (newBars.bars[i].height<newBars.bars[i+1].height){
            sorted=false
            a++
            const b=i
            setTimeout( () => {
                let temp=newBars.bars[b].height
                newBars.bars[b].height=newBars.bars[b+1].height
                newBars.bars[b+1].height=temp
                newBars.bars[b].color=`red`
                newBars.bars[b+1].color=`blue`
                this.setState(newBars)
                },13*b)
            setTimeout( () => {
                newBars.bars[b].color=`black`
                newBars.bars[b+1].color=`black`
                this.setState(newBars)
                }, 13*b+30)
            }
        }
        a++
        setTimeout( () => sorted === false ? this.sortBars(): null, 17*a)
    }

    render(){
        return(
            <>
            <button onClick={this.sortBars}>Sort</button>
            {this.state.bars.map( (val,index) => this.renderBar(val,index))}

            </>
        );
    }
}

