import React from 'react';
import bars from './generateBars.js';
var a=0;
class Bars extends React.Component{
    constructor(){
        super()
        this.state={bars: bars.map( height => [height,`black`])}



        this.sortBars=this.sortBars.bind(this)
        this.swap=this.swap.bind(this)
    }


    swap(newBars,a,b){
        console.log(this.state)
        var temp=newBars[b][0]
        newBars[b][0]=newBars[a][0]
        newBars[a][0]=temp
        newBars[b][1]=`red`
        newBars[a][1]=`blue`
        this.setState({bars: newBars})
        a++;
        console.log(a++)
    }


    swap2(newBars,a,b){
        newBars[b][1]=`black`
        newBars[a][1]=`black`
        this.setState({bars: newBars})

    }

    sortBars(){
        var sorted=true
        var a=1
        var newBars=this.state.bars
        for (var i=0; i<=this.state.bars.length-2;i++){
            if (this.state.bars[i][0]<this.state.bars[i+1][0]){
                sorted=false
                a++
                const b=i
                setTimeout( () => this.swap(newBars,b,b+1), 10*a)
                setTimeout( () => this.swap2(newBars,b,b+1), 15*a)
            }


        }
        a++
        setTimeout( () => sorted === false ? this.sortBars(): null, 15*a)

    }

    render(){
        return(
            <>
            <button onClick={this.sortBars}>Sort</button>
            {this.state.bars.map((val,index) => (
                <div className={index} key={index} style={{background: val[1], marginRight: `10px`, height: val[0], float: 'left', width: `15px`}}>   
                </div>
            ))}

            </>
        );
    }
}

export default Bars
