import React from 'react';
import bars from './generateBars.js';

class Bars extends React.Component{
    constructor(){
        super()
        this.state={bars: bars.map( height => [height,`black`])}



        this.sortBars=this.sortBars.bind(this)
        this.swap=this.swap.bind(this)
    }


    swap(newBars,a,b){
        const aBar = document.getElementsByClassName(a);
        const bBar = document.getElementsByClassName(b);
        var temp=newBars[b][0]
        newBars[b][0]=newBars[a][0]
        newBars[a][0]=temp
        aBar[0].style.background='red';
        bBar[0].style.background='blue';
        this.setState({bars: newBars})
    }


    swap2(newBars,a,b){
        const aBar = document.getElementsByClassName(a);
        const bBar = document.getElementsByClassName(b);
        aBar[0].style.background='black';
        bBar[0].style.background='black';
        this.setState({bars: newBars})
    }

    sortBars(){
        var sorted=true
        var a=1
        var newBars=this.state.bars
        for (var i=0; i<=this.state.bars.length-2;i++){
            if (this.state.bars[i][0]<this.state.bars[i+1][0]){
                sorted=false
                a++
                // const b=i
                setTimeout( () => this.swap(newBars,i,i+1), 20*a)
                setTimeout( () => this.swap2(newBars,i,i+1), 30*a)
            }


        }

        setTimeout( () => sorted == false ? this.sortBars(): null, 20*a)

    }

    render(){
        return(
            <>
            <button onClick={this.sortBars}>Sort</button>
            {this.state.bars.map((val,index) => (
                <div className={index} key={index} style={{background: val[1], marginRight: `10px`, height: val[0], float: 'left', width: `10px`}}>   
                </div>
            ))}

            </>
        );
    }
}

export default Bars
