export default function merge_sort_animations(arr){
if (arr.length <= 1) return;
let animations=[]
merge(arr,animations)

return animations
}

function merge(arr,animations){
    let midIndx=Math.floor(arr.length/2)
    let L = arr.slice(0,midIndx)
    let R= arr.slice(midIndx)
    merge(L,animations)
    merge(R,animations)
    let i,j,k=0

    while(i<L.length && j<R.length){
        if (L[i]>R[j]){
            arr[k]=L[i]
            i++
        }
        else {
            arr[k]=R[j]
            j++
        }
        k++

    }

    while (i<L.length){
        arr[k]=L[i]
        i++
        k++
    }

    while (j<R.length){
        arr[k]=R[j]
        j++
        k++
    }
    return arr
}
