export default function insertion_sort_animations(arr){
    let animations=[]
    for (let i=1;i<arr.length-1;i++){
        let j=i
        while (j>0 && arr[j-1]<arr[j]){
            let temp=arr[j-1]
            arr[j-1]=arr[j]
            arr[j]=temp
            animations.push([i,j])
            animations.push([i,j])
            animations.push([i,j])
            j--
        }
    }
    return animations
}
