import React from 'react';
import bars from './generateBars.js';


class Bars extends React.Component{
    constructor(){
        super()
        this.state={bars: bars.map( height => [height,`black`])}
        console.log(this.state);



        this.sortBars=this.sortBars.bind(this)
    }

    sortBars(){
        var newBars= this.state.bars
        // console.log(newBars)
        // for (var i=0; i<=this.state.bars.length-1;i++){

        setTimeout(() => newBars[0]=[this.state.bars[0],`red`],10020)
        this.setState({bars:newBars})


    }

    render(){
        return(
            <>
            <button onClick={this.sortBars}>Sort</button>
            {this.state.bars.map((val,index) => (
                <div key={index} style={{background: val[1], marginRight: `20px`, height: val[0], float: 'left', width: `20px`}}>   
                </div>
            ))}

            </>
        );
    }
}

