import React from 'react';
import Bar from './Bar.js';
import generateBars from './generateBars.js'
import get_bubble_sort_animations from './sorting-algo/bubble_sort.js'
import get_merge_sort_animations from './sorting-algo/merge_sort.js'
import get_insertion_sort_animations from './sorting-algo/insert_sort.js'
import get_quick_sort_animations from './sorting-algo/quick_sort.js'
import get_heap_sort_animations from './sorting-algo/heap_sort.js'

const ANIMATION_SPEED=10

class Bars extends React.Component{
    constructor(){
        super()
        this.state={bars: generateBars(50),color:[],timeOuts:[]}
        this.state.resetBars=this.state.bars.slice()
        this.state.color=Array(this.state.bars.length).fill('black')

        this.sortBars=this.sortBars.bind(this)
    }

    renderBar(val,index){
        return (
        <Bar key={index} val={val} color={this.state.color[index]}/>
        );
    }
    get_animations(sortType){
        switch(sortType){
            case "bubble": return get_bubble_sort_animations(this.state.bars.slice())
            case "merge": return get_merge_sort_animations(this.state.bars.slice())
            case "insert": return get_insertion_sort_animations(this.state.bars.slice())
            case "quick": return get_quick_sort_animations(this.state.bars.slice())
            case "heap": return get_heap_sort_animations(this.state.bars.slice())
            default: return
        }
    }

    clearTimouts(){
        for (let id in this.state.timeOuts){
            console.log(id)
            clearTimeout(id)
        }
        this.setState({bars: this.state.resetBars.slice(),timeOuts:[],color:Array(this.state.bars.length).fill('black')})

    }

    sortBars(sortType){
        let copyBars=this.state.bars.slice()
        let copyColor=this.state.color.slice()
        let animations=[]
        let timeOut;

        animations=this.get_animations(sortType)
        animations.forEach( (value,index) =>{
            if (index % 3 === 0){
                timeOut=setTimeout( () => {
                    copyColor[value[0]]=`red`
                    copyColor[value[1]]=`blue`
                    this.setState({color:copyColor})
                },ANIMATION_SPEED*index)
            }
            else if (index % 3 === 1 ){
                timeOut=setTimeout( () => {

                    if (sortType === "merge"){
                        copyBars[value[0]]=value[1]   
                    }
                    else{
                        swap(copyBars,value[0],value[1])
                        copyColor[value[0]]=`green`
                        copyColor[value[1]]=`green`
                    }

                    this.setState({bars:copyBars,color:copyColor})
                },ANIMATION_SPEED*index+ANIMATION_SPEED/2)
            }
            else{
                timeOut=setTimeout( () => {
                    copyColor[value[0]]=`black`
                    copyColor[value[1]]=`black`
                    this.setState({color:copyColor})
                }, ANIMATION_SPEED*index+ANIMATION_SPEED)
            }
            this.setState({timeOuts:this.state.timeOuts.slice().push(timeOut)}) // adding each of the timeouts to the state
            });    
    }

    render(){
        return(
            <>
            <button onClick={() => this.setState({bars: this.state.resetBars.slice()})}>Reset</button>
            <button onClick={() => this.sortBars("bubble")}>Bubble Sort</button>
            <button onClick={() => this.sortBars("merge")}>Merge Sort</button>
            <button onClick={() => this.sortBars("insert")}>Insertion Sort</button>
            <button onClick={() => this.sortBars("quick")}>Quick Sort</button>
            <button onClick={() => this.sortBars("heap")}>Heap Sort</button>
            <button onClick={() => this.clearTimouts()}>Stop</button>
            {this.state.bars.map( (val,index) => this.renderBar(val,index))}

            </>
        );
    }
}

export function swap(arr,i,k){
    let temp=arr[i]
    arr[i]=arr[k]
    arr[k]=temp
}

export default Bars
