function merge(arr,auxiliaryArr,animations,counter){
    if (auxiliaryArr.length > 1){
        let midIndx=Math.floor(auxiliaryArr.length/2)
        let L = auxiliaryArr.slice(0,midIndx)
        let R= auxiliaryArr.slice(midIndx)
        console.log(L,R,counter[0])
        merge(arr,L,animations,counter)
        merge(arr,R,animations,counter)
        let i,j=0

        while(i<L.length && j<R.length){
            if (L[i]>R[j]){
                arr[counter[0]]=L[i]
                i++
            }
            else {
                arr[counter[0]]=R[j]
                j++
            }
            counter[0]++

        }

        while (i<L.length){
            arr[counter[0]]=L[i]
            i++
            counter[0]++
        }

        while (j<R.length){
            arr[counter[0]]=R[j]
            j++
            counter[0]++
        }
    }
}
